select o.*,u.*,a.*,s.name as storeName from orders o,user u,address a,store s where o.user_id = u.id and o.address_id = a.id and o.storeid = s.id
and o.id like CONCAT('%','${key}','%') or u.nickname like CONCAT('%','${key}','%') or a.address like CONCAT('%','${key}','%')
and s.id = #{storeid}
and o.status = #{status}
order by o.date DESC,o.status ASC

